nta.declaration:
// Place global declarations here.
const int AgentNum = 2;
typedef int[0,AgentNum-1] AgentScale;
const int TaskNum = 5;
const int EventNum = 0;
const int MilestoneNum = 7;
const int MaxIteration = 1;

bool position[AgentNum][MilestoneNum]={{false,false,false,false,false,false,false},{false,false,false,false,false,false,false}};
bool tf[AgentNum][TaskNum]={{true,false,false,false,false},{true,false,false,false,false}};
bool ts[AgentNum][TaskNum]={{true,false,false,false,false},{true,false,false,false,false}};


bool isBusy(int taskID)
{
    int id = 0;
    bool busy = false;

    for(id = 0; id < AgentNum; id++)
    {
        if(ts[id][taskID])
        {
            busy = true;
            return busy;
        }
    }

    return busy;
}

const int BCET[AgentNum][TaskNum]={{0,5,5,6,5},{0,5,5,6,5}};
const int WCET[AgentNum][TaskNum]={{0,15,10,8,12},{0,15,10,8,12}};
const int RegularTaskNum[AgentNum] = {4,4};
//chan move[AgentNum], initialize;

int iteration[AgentNum] = {0,0};

clock globalTime;

const int MAXTIME = 5000;
bool idle[AgentNum] = {true,true};

template.declaration:
clock t;
bool isReady()
{
    return idle[id]&&iteration[id]<MaxIteration;
}

clock t;

void updateIteration()
{
	int i=0;
	bool finish = true;
	for(i=1;i<=RegularTaskNum[id];i++)
	{
		finish &= tf[id][i];
	}
	if(finish)
	{
		for(i=1;i<=RegularTaskNum[id];i++)
		{
			tf[id][i]=false;
		}
		
		iteration[id]+=1;
		if(iteration[id]>=MaxIteration)
		{
			iteration[id]=MaxIteration;
		}
	}
	else
	{
    	finish = true;
	}
}

bool isReady(int taskID)
{
    return !tf[id][taskID]&&iteration[id]<MaxIteration;
}

