nta.declaration:
// Place global declarations here.
const int AgentNum = 2;
typedef int[0,AgentNum-1] AgentScale;
const int TaskNum = 7;
const int EventNum = 0;
const int MilestoneNum = 7;
const int MaxIteration = 1;

bool position[AgentNum][MilestoneNum]={{true,false,false,false,false,false,false},{true,false,false,false,false,false,false}};
bool tf[AgentNum][TaskNum]={{true,false,false,false,false,false,false},{true,false,false,false,false,false,false}};
bool ts[AgentNum][TaskNum]={{true,false,false,false,false,false,false},{true,false,false,false,false,false,false}};


bool isBusy(int taskID)
{
    int id = 0;
    bool busy = false;

    for(id = 0; id < AgentNum; id++)
    {
        if(ts[id][taskID])
        {
            busy = true;
            return busy;
        }
    }

    return busy;
}

const int rules[MilestoneNum]={0,1,2,3,4,5,6};
const int RegularTaskNum[AgentNum] = {6,6,//chan move[AgentNum], initialize;

int iteration[AgentNum] = {0,0,
clock globalTime;

const int MAXTIME = 5000;
bool idle[AgentNum] = {true,true};

int penalty = 0; 
void punish(int agentID)
{
    if(penalty <= MAXTIME)
    {
        penalty = penalty + 10;
    }

    idle[agentID] = false;
}
bool isNecessary(int agentID, int taskID)
{
    return !tf[agentID][taskID]&&iteration[agentID]<MaxIteration;
}
bool isReady(int agentID, int currentPosition, int targetPosition)
{
    int currentTask = rules[currentPosition];
    int targetTask = rules[targetPosition];
    bool ready = idle[agentID]&&iteration[agentID]<MaxIteration;
    bool currentFinished = tf[agentID][currentTask];
    bool targetFinished = tf[agentID][targetTask];

    return ready&&currentFinished&!targetFinished;
}

template.declaration:
clock t;

void updateIteration()
{
	int i=0;
	bool finish = true;
	for(i=1;i<=RegularTaskNum[id];i++)
	{
		finish &= tf[id][i];
	}
	if(finish)
	{
		for(i=1;i<=RegularTaskNum[id];i++)
		{
			tf[id][i]=false;
		}
		
		iteration[id]+=1;
		if(iteration[id]>=MaxIteration)
		{
			iteration[id]=MaxIteration;
		}
	}
	else
	{
    	finish = true;
	}
}

void reward()
{
    int times = 0, i = 0;

    for(i = 0; i < TaskNum; i++)
    {
        if(tf[id][i])
        {
            times++;
        }
    }

    penalty = penalty - 100 * times;
}

